import React, { Component } from "react";
import { Card, CardImg, CardText, CardBody, CardTitle, Breadcrumb, BreadcrumbItem,
	 Button, Modal, ModalHeader, ModalBody, Row, Label, Input } from 'reactstrap';
import { Control, LocalForm, Errors } from 'react-redux-form';
import { Link } from 'react-router-dom';

	class CommentForm extends Component {
		constructor(props) {
			super(props);
		  
			this.state = {
				isModalOpen: false
			};
		
			this.toggleModal = this.toggleModal.bind(this);
			this.handleSubmit = this.handleSubmit.bind(this);
	}

	toggleModal() {
        this.setState({
          isModalOpen: !this.state.isModalOpen
        });
    }

	handleSubmit(values) {
        console.log('Current State is: ' + JSON.stringify(values));
        alert('Current State is: ' + JSON.stringify(values));
    }

	render (){
		return (
			<div>
      			<Button outline onClick={this.toggleModal}><span className="fa fa-pencil fa-lg"></span> Submit Comment</Button>
				<Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Login</ModalHeader>
                    <ModalBody>
                        <LocalForm onSubmit={(values) => this.handleSubmit(values)}>
							<Row className="form-group">
								<Label htmlFor="rating">Rating</Label>
								<Control.select model=".contactType" name="contactType"
											className="form-control">
											<option>Tel.</option>
											<option>Email</option>
								</Control.select>
                            </Row>
                            <Row className="form-group">
                                <Label htmlFor="password">Password</Label>
                                <Input type="password" id="password" name="password"
                                    innerRef={(input) => this.password = input}  />
                            </Row>
                            <Row className="form-group">
                                <Label check>
                                    <Input type="checkbox" name="remember"
                                    innerRef={(input) => this.remember = input}  />
                                    Remember me
                                </Label>
                            </Row>
                            <Row className="form-group">
                                    <Button type="submit" color="primary">
                                    Submit
                                    </Button>
                            </Row>
                        </LocalForm>
                    </ModalBody>
                </Modal>
			</div>
		)
	}
}

	function RenderComments({comments}) {
        if (comments != null) {
            const commentsList = comments.map(review => {
                return (
                    <li key = {review.id}>
                        <p>{review.comment}</p> 
                        <p>-- {review.author} , {new Intl.DateTimeFormat("en-US", {year: "numeric", month: "short", day: "2-digit"}).format(new Date(Date.parse(review.date)))}</p>
                    </li>
                );
            });
            return (
				<React.Fragment>
                    <h4>Comments</h4>
					<ul className="list-unstyled">
						{commentsList}
					</ul>
				</React.Fragment>
            );
		}
		else
			return (
				<div></div>
			);
    }

	function RenderDish({dish}) {
        if (dish != null) {
            return (
				<Card>
					<CardImg width="100%" src={dish.image} alt={dish.name} />
					<CardBody>
						<CardTitle><h5>{dish.name}</h5></CardTitle>
						<CardText>{dish.description}</CardText>
					</CardBody>
				</Card>
            );
		}
		else {
            return (
                <div></div>
            );
        }
    }

	const DishDetail = (props) => {
		const dish = props.dish;
		if (dish != null) {
			return (
                <div className="container">
					<div className="row">
						<Breadcrumb>
							<BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
							<BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
						</Breadcrumb>
						<div className="col-12">
							<h3>{props.dish.name}</h3>
							<hr />
						</div>                
					</div>
					<div className="row">
						<div className="col-12 col-md-5 m-1">
							<RenderDish dish={props.dish} />
						</div>
						<div className="col-12 col-md-5 m-1">
							<RenderComments comments={props.comments} />
							<CommentForm />
						</div>
					</div>
                </div>
            );
		} 
		else 
			return (
				<div></div>
			);
	}

export default DishDetail;
