import React from "react";
import { Card, CardImg, CardText, CardBody, CardTitle, Breadcrumb, BreadcrumbItem } from 'reactstrap';
import { Link } from 'react-router-dom';

	function RenderComments({comments}) {
        if (comments != null) {
            const comment = comments.map(review => {
                return (
                    <li key = {review.id}>
                        <p>{review.comment}</p> 
                        <p>-- {review.author} , {new Intl.DateTimeFormat("en-US", {year: "numeric", month: "short", day: "2-digit"}).format(new Date(Date.parse(review.date)))}</p>
                    </li>
                );

            });
            return (
                <div className="col-12 col-md-5 m-1">
                    <h4>Comments</h4>
					<ul className="list-unstyled">
						{comment}
					</ul>
                </div>
            );
		}
		else
			return (
				<div></div>
			);
    }

	function RenderDish({dish}) {
        if (dish != null) {
            return (
				<div className="col-12 col-md-5 m-1">
					<Card>
						<CardImg width="100%" src={dish.image} alt={dish.name} />
						<CardBody>
							<CardTitle><h5>{dish.name}</h5></CardTitle>
							<CardText>{dish.description}</CardText>
						</CardBody>
					</Card>
				</div>
            );
		}
		else {
            return (
                <div></div>
            );
        }
    }

	const DishDetail = (props) => {
		const dish = props.dish;
		if (dish != null) {
			const dishItem = <RenderDish dish={props.dish} />
        	const commentItem = <RenderComments comments={props.dish.comments} />
			return (
				<div className="container">
					<div className="row">
						{dishItem}
						{commentItem}
					</div>
				</div>
			);
		} 
		else 
			return (
				<div></div>
			);
	}

export default DishDetail;
