	render() {
		const dish = this.props.dish;
		if (dish != null) {
			const dishItem =  this.renderDish(dish);
        	const commentItem = this.renderComments(dish.comments);
			return (
				{dishItem},
                {commentItem}
			);
		} 
		else 
			return(
				<div></div>
			);
	}
}

export default DishDetail;
