import React, { Component } from 'react';
import { Card, CardImg, CardImgOverlay, CardText, CardBody, CardTitle } from 'reactstrap';

class Menu extends Component {
    
    constructor(props){
        super(props); // required when you define class component

        //state keeps the related propoties 
        this.state = {
            selectedDish: null
        }
    }

    onDishSelect(dish) {
        this.setState({selectedDish: dish});
    }
     
    renderDish(dish){
        if(dish != null){
             return (
                <Card>
                   <CardImg width="100%"  src={dish.image} alt={dish.name} />
                   <CardBody>
                     <CardTitle heading>{dish.name}</CardTitle>
                     <CardText>{dish.description}</CardText>

                    </CardBody>  
                </Card>
             );
        }else{
            return(
                <div></div> //nothing will be rendered
            )
        }
    }
    render() {
// view part of the app
  // get the dishes arry in the state and ittrete
       const menu = this.props.dishes.map((dish) => {
           return(
               <div key ={dish.id} className="col-12 col-md-5 md-1">
                  <Card onClick={() => this.onDishSelect(dish)}>
                    <CardImg width="100%"  src={dish.image} alt={dish.name} />
                      <CardImgOverlay>
                          <CardTitle heading>{dish.name}</CardTitle>
                   
                      </CardImgOverlay>
                  </Card>
               </div>
           );
       }); 
     
        return(
             <div className="container">
                 <div className="row">
                      {menu}
                 </div>
                 <div className="row">
                     {this.renderDish(this.state.selectedDish)}
                 </div>
             </div>
        );
    }
}

export default Menu; // for importing purpose in future
