import React, { Component } from "react";
import { Card, CardImg, CardText, CardBody, CardTitle, ListGroup, ListGroupItem, ListGroupItemText } from "reactstrap";

class DishDetail extends Component {
	render() {
		const dish = this.props.dish;
		if (dish != null) {
			return (
				<div className="row">
					<div className="col-12 col-md-5 m-1">
						<Card>
							<CardImg top src={dish.image} alt={dish.name} />
							<CardBody>
								<CardTitle><h5>{dish.name}</h5></CardTitle>
								<CardText>{dish.description}</CardText>
							</CardBody>
						</Card>
					</div>

					<div className="col-12 col-md-5 m-1">
						<h4>Comments</h4>
						{dish.comments.map((comment) => {
							const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
							const date = comment.date;
							return (
								<ListGroup key={comment.id} className="list-unstyled">
									<ListGroupItem>
										<ListGroupItemText>{comment.comment}</ListGroupItemText>
										<ListGroupItemText>-- {comment.author}, {comment.date}</ListGroupItemText>
									</ListGroupItem>
								</ListGroup>
							);
						})}
					</div>
				</div>
			);
		} 
		else 
			return(
				<div></div>
			);
	}
}

export default DishDetail;
