import React,{Component} from 'react'
import {Card, CardImg, CardText, CardTitle, BreadcrumbItem, Breadcrumb, Label, Button,
   Modal, ModalBody, ModalHeader, Row} from 'reactstrap'

import {Control, Errors, LocalForm} from 'react-redux-form'
import {Link} from 'react-router-dom';


const required = (val) => val && val.length;
const maxLength = (len) => (val) => !(val) || (val.length <= len);
const minLength = (len) => (val) => (val) && (val.length >= len);

class CommentForm extends Component{
  state = {
    isModalOpen : false,
  }

  toggleModal = () => {
    let show = this.state.isModalOpen;
    this.setState({
      isModalOpen : !show,
    })
  }

  submitComment = (value) => {
    alert(JSON.stringify(value));
    this.toggleModal();
  }

  render(){
    return(
        <>
          <div>
            <Button onClick={this.toggleModal} type='submit' outline><span className='fa fa-pencil'></span> Submit Comment</Button>
          </div>

          <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
            <ModalHeader toggle={this.toggleModal}>Submit Comment</ModalHeader>
            <ModalBody>

              <LocalForm onSubmit={(value) => this.submitComment(value)}>
                <Row className='form-group m-1'>
                  <Label htmlFor='rating' md={12}>Rating</Label>
                  <Control.select model=".ratinh" name="Rating" className="form-control">
                    <option>1</option>
                    <option>2</option>
                    <option>3</option>
                    <option>4</option>
                    <option>5</option>
                  </Control.select>
                </Row>

                <Row className='form-group m-1'>
                  <Label htmlFor='rating' md={12}>Your Name</Label>
                  <Control.text model='.authorName' id='authorName' name='authorName' className='form-control' 
                  validators = {{
                    required, minLength : minLength(2), maxLength : maxLength(15)
                  }}
                  />

                  <Errors 
                    className='text-danger'
                    model = '.authorName'
                    show = 'touched'
                    messages={{
                        required : 'Required ',
                        minLength : 'Must be greater than 2 character',
                        maxLength : 'Must be 15 characters or less'
                    }}                                
                  />
                </Row>

                <Row className='form-group m-1'>
                  <Label htmlFor='comment' md={12}>Comment</Label>
                  <Control.textarea model='.comment' id='comment' name='comment' className='form-control'
                  rows="6"/>
                </Row>             

                <Button type="submit" value="submit" color="primary">Submit</Button>
              </LocalForm>
            </ModalBody>
          </Modal>
        </>
    )
  }
}


const  Dishdetail = (props) => {

    const comment = props.comments.map((item, index) => {
      return(
        <li key={index}>
          <p>--{item.author}, {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'long', day: '2-digit' }).format(new Date(item.date))}</p>
          <p>{item.comment}</p>
        </li>
      )
    })    

    return(  
      <div className='container'>
        <div className='row'>
          <Breadcrumb>
            <BreadcrumbItem><Link to='/menu'>Menu</Link></BreadcrumbItem>
            <BreadcrumbItem active ><Link to='/{props.dish.name}'>{props.dish.name}</Link></BreadcrumbItem>
          </Breadcrumb>
          <div className='col-12'>
            <h3>{props.dish.name}</h3>
            <hr />
          </div>
        </div>
        <div className='row'>
          <Card className='col-12 col-md-5 m-1'>
            <CardImg src={props.dish.image} alt={props.dish.name} width='100%'></CardImg>
            <CardTitle>{props.dish.name}</CardTitle>
            <CardText>{props.dish.description}</CardText>
          </Card>

          <Card className='col-12 col-md-5 m-1'>
            <h4>Comments</h4>
              {comment}
              <ul className='list-unstyled'>
                <CommentForm/>               
              </ul>
          </Card>
        </div>  
        
        
        
      </div>
    )
    
}

export default Dishdetail;
